// frontend/src/contexts/AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode';
import toast from 'react-hot-toast';

const AuthContext = createContext();

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [token, setToken] = useState(null);

    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            try {
                const decodedToken = jwtDecode(storedToken);

                // Check if token is expired
                if (decodedToken.exp * 1000 > Date.now()) {
                    setToken(storedToken);
                    setUser({
                        id: decodedToken.userId,
                        username: decodedToken.username,
                        email: decodedToken.email,
                        role: decodedToken.role
                    });
                } else {
                    localStorage.removeItem('token');
                    toast.error('Session expired. Please login again.');
                }
            } catch (error) {
                console.error('Invalid token:', error, storedToken);
                localStorage.removeItem('token');
                toast.error('Invalid session. Please login again.');
            }
        }
        setLoading(false);
    }, []);

    const login = (newToken, userData) => {
        console.log('Login called with:', { newToken: newToken ? 'present' : 'missing', userData });
        if (!newToken || !userData || !userData.username || !userData.email || !userData.id) {
            console.error('Invalid login payload:', { newToken, userData });
            toast.error('Login failed: invalid user data returned from server.');
            return;
        }
        localStorage.setItem('token', newToken);
        setToken(newToken);
        setUser(userData);
        console.log('User logged in successfully:', userData);
        toast.success(`Welcome back, ${userData.username}!`);
    };

    const logout = () => {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        toast.success('Logged out successfully');
    };

    const getAuthHeader = () => {
        return token ? { Authorization: `Bearer ${token}` } : {};
    };

    const updateUser = (userData) => {
        setUser(prev => ({ ...prev, ...userData }));
    };

    const value = {
        user,
        token,
        login,
        logout,
        updateUser,
        getAuthHeader,
        isAuthenticated: !!user,
        loading
    };

    console.log('AuthContext state:', { user, isAuthenticated: !!user, loading });

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
};
